INSERT INTO `movie` (`id`, `name`, `name_native`, `poster`, `release_year`, `release_date`, `description`, `alias`) VALUES
(1, 'Одинокий рейнджер', 'The Lone Ranger', 'lone-ranger.jpg', 2013, '2013-06-22', 'История блюстителя закона Джона Рида, который с помощью индейца Тонто стал легендарным мстителем в маске, стоящим на защите справедливости. Тонто с юмором и небылицами повествует о тех приключениях, которые пришлось пережить двум непохожим друг на друга героям, сведенным судьбой для того, чтобы вместе сражаться против общего врага. Им приходится противостоять жадности и коррупции во времена, когда появление первых железных дорог изменило представление о власти и могуществе в мире.', 'lone_ranger'),
(2, 'Ранго', 'Rango', 'rango.jpg', 2011, '2011-02-14', 'Ранго - хамелеон, который живет в террариуме и считает себя героем, которому, к сожалению, никак не удается проявить свое бесстрашие. Но когда он внезапно оказывается в городке Грязь, у него появляется такая возможность. Ранго провозглашает себя борцом за справедливость и начинает вести себя как шериф на Диком Западе. Он еще не знает, что быть «хорошим парнем» в этих краях не самая завидная участь…', 'rango'),
(3, 'Капитан Филлипс', 'Captain Phillips', 'captain-phillips.jpg', 2013, '2013-09-27', 'Фильм основан на реальных событиях, произошедших в апреле 2009 года в открытом море недалеко от берегов Африки.\nКапитан грузового судна Ричард Филлипс сдается в плен группе сомалийских пиратов, чтобы защитить свой экипаж.', 'captain_phillips');

